import React, { useState } from 'react';
import img from '../assets/images (4).jpeg';

const Cart = () => {
  const [num, setNum] = useState(1);  // Corrected useState declaration

  return (
    <div className="m-10 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
      <div>
        <div className="bg-white rounded-lg shadow-md h-96 p-5">
          <div className="h-48">
            <img src={img} alt="Product" className="w-full h-full object-cover hover:scale-105 transition-transform" />
          </div>
          <div className="flex items-center justify-between mt-10">
            <p className='font-bold text-xl'>$34</p>
            <input type="number" value={num} onChange={(e) => setNum(e.target.value)} // Updates quantity on input changeclassName='w-20 text-xl border-black border-2 rounded-lg ps-2 shadow-md'
            />
          </div>
          <button className='text-center bg-gray-800 text-white w-full mt-5 py-3 rounded-lg hover:bg-gray-600'>
            Check Out
          </button>
        </div>
      </div>
    </div>
  );
};

export default Cart;
