import React, { useState, useEffect } from 'react';
import { useContext } from 'react';
import { UserContext } from '../context/userContext';
import { Link, useNavigate } from 'react-router-dom';
import Loading from '../components/Loading';
import firstTrouser from '../productsassets/download (1).jpeg';
import firstShirt from '../productsassets/download (2).jpeg';
import firstWristWatch from '../productsassets/download.webp';
import firstShoes from '../productsassets/download (3).jpeg';

const Shop = () => {
  const navigate = useNavigate();
  const [categoriesOpen, setCategoriesOpen] = useState(false); 
  const { user } = useContext(UserContext);
  const [loading, setLoading] = useState(true);

  const categoriesSelect = () => {
    setCategoriesOpen(!categoriesOpen);
  };

  const products = [
    {
      imgSrc: firstTrouser,
      category: 'trouser',
      price: '$20',
    },
    {
      imgSrc: firstShirt,
      category: 'shirt',
      price: '$20',
    },
    {
      imgSrc: firstWristWatch,
      category: 'wristwatch',
      price: '$20',
    },
    {
      imgSrc: firstShoes,
      category: 'shoe',
      price: '$20',
    },
  ];

  useEffect(() => {
    const timer = setTimeout(() => {
      setLoading(false);
    }, 4000);
    return () => clearTimeout(timer); 
  }, []);

  useEffect(() => {
    if (!user) navigate('/login');
  }, [user, navigate]);

  return (
    <div>
      <div className='relative flex justify-between bg-white items-center sm:p-5 py-4 px-1 shadow-lg'>
        {/* Categories, Search, and Cart Code */}
      </div>
      
      {loading ? (
        <Loading />
      ) : (
        <div className="p-4">
          {/* Grid layout for products */}
          <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
            {products.map((product, index) => (
              <div key={index} className="bg-white w-full rounded-lg shadow-md h-80 p-5">
                <div className="h-48">
                  <img src={product.imgSrc} alt="" className="w-full h-full object-cover hover:scale-105 transition-transform" />
                </div>
                <div className="flex items-center justify-between mt-10">
                  <p>{product.price}</p>
                  <button className="bg-gray-800 hover:bg-gray-600 text-white py-1 rounded-sm w-28">Add to Cart</button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default Shop;
