// Navbar.js
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useUser } from '../UserContext';

const Navbar = () => {
    const [navOpen, setOpen] = useState(false);
    const [logoutOpen, setLogoutOpen] = useState(false);
    const { user, setUser } = useUser();

    const handleLogout = () => {
        setUser(null); // Clear user data
        setLogoutOpen(false);
    };

    return (
        <div className='flex justify-between items-center bg-gray-800 px-5 text-white border-b-2 border-yellow-700 sticky inset-0 sm:px-28 py-5'>
            <h2 className='text-3xl'>E<span className='text-yellow-700 font-bold'>SHOP</span></h2>
            
            <div className='hidden sm:inline-flex gap-4 text-xl items-center'>
                {user ? (
                    <div  className='relative' onClick={() => setLogoutOpen(!logoutOpen)} 
                    >
                        <div className='flex items-center gap-3 cursor-pointer'>
                            <p className="text-white">{user.name}</p>
                            <i className="fa-solid fa-caret-down"></i>
                        </div>
                        {logoutOpen && (
                            <div className='absolute top-10 left-0 bg-white text-center hover:bg-slate-400 text-gray-800 rounded-lg w-full shadow-lg py-2 px-4'>
                                <Link onClick={handleLogout} to="/login">Log Out</Link>
                            </div>
                        )}
                    </div>
                ) : (
                    <Link to="/login" className='text-xl'>
                        <i className="fa-solid fa-image-portrait"></i> Login
                    </Link>
                )}
                <Link to="/">Home</Link>
                <Link to="/shop">Shop</Link>
            </div>

            <i 
                className={`fa-solid ${!navOpen ? 'fa-bars' : 'fa-xmark'} text-2xl sm:hidden cursor-pointer`} 
                onClick={() => setOpen(!navOpen)}
            ></i>

            {navOpen && (
                <div className='absolute left-0 top-16 w-full bg-gray-800 border-b-2 border-yellow-700 z-10 flex flex-col items-center gap-4 text-xl text-white py-5'>
                    <Link to="/login" onClick={() => setOpen(!navOpen)}>Login</Link>
                    <Link to="/" onClick={() => setOpen(!navOpen)}>Home</Link>
                    <Link to="/shop" onClick={() => setOpen(!navOpen)}>Shop</Link>
                </div>
            )}
        </div>
    );
};

export default Navbar;
