import React, { useState, useEffect } from 'react';
import img from '../assets/images (4).jpeg';
import { useContext } from 'react'
import { UserContext } from '../context/userContext'
import { useNavigate } from 'react-router-dom'

const Cart = () => {
  const navigate = useNavigate();
  const [num, setNum] = useState(1); 
  const basePrice = 34; 
  const { user, carts } = useContext(UserContext)

  const numChanging = (e) => {
    setNum(e.target.value);
  };
  
  useEffect(() => {
    if (!user) navigate('/login')
    // eslint-disable-next-line
  }, [user])

  return (
    <div className="m-10 grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 gap-4">
      {
        carts.map(item => (
          <div className="bg-white rounded-lg shadow-md h-96 p-5">
          <div className="h-48">
            <img src={item?.img} alt="Product" className="w-full h-full object-cover hover:scale-105 transition-transform" />
          </div>
          <div className="flex items-center justify-between mt-10">
            <p className='font-bold text-xl'>${item?.price * num}</p>
            <input type="number" value={num} onChange={numChanging} min="1" className='w-14 text-xl border-black border-2 rounded-lg ps-2 shadow-md'/>
          </div>
          <button className='text-center bg-gray-800 text-white w-full mt-5 py-3 rounded-lg hover:bg-gray-600'>
            Check Out
          </button>
        </div>
        ))
      }
    </div>
  );
};

export default Cart;
