import { createContext, useState } from "react";
import { toast } from "react-toastify";
import { useNavigate } from "react-router-dom";
import firstTrouser from '../productsassets/download (1).jpeg'
import firstShirt from '../productsassets/download (2).jpeg'
import firstWristWatch from '../productsassets/download.webp'
import firstShoes from '../productsassets/download (3).jpeg'
import firstElectronics from '../productsassets/download (4).jpeg'



export const UserContext = createContext(null)

const UserProvider = ({children}) => {
    const [user, setUser] = useState(null)
    const [cartQuantity, setCartQuantity] = useState(0)
    const [products] = useState([
      {
        imgSrc: firstTrouser,
        category: 'trouser',
        price: '$20'
      },
      {
        imgSrc: firstShirt,
        category: 'shirt',
        price: '$34'
      },
      {
        imgSrc: firstWristWatch,
        category: 'wristwatch',
        price: '$45'
      },
      {
        imgSrc: firstShoes,
        category: 'shoe',
        price: '$56'
      },
      {
        imgSrc: firstElectronics,
        category: 'electronic',
        price: '$100'
      },
    ])

    const setToCart = () => {
      setCartQuantity(cartQuantity => cartQuantity + 1)
      
    }
    function addContent(){
      const html = `
        <div className="bg-white rounded-lg shadow-md h-96 p-5">
          <div className="h-48">
            <img src={img} alt="Product" className="w-full h-full object-cover hover:scale-105 transition-transform" />
          </div>
          <div className="flex items-center justify-between mt-10">
            <p className='font-bold text-xl'>${basePrice * num}</p>
            <input type="number" value={num} onChange={numChanging} min="1" className='w-20 text-xl border-black border-2 rounded-lg ps-2 shadow-md'/>
          </div>
          <button className='text-center bg-gray-800 text-white w-full mt-5 py-3 rounded-lg hover:bg-gray-600'>
            Check Out
          </button>
        </div>
`
    }

    const navigate = useNavigate()
    const baseUrl = "http://localhost:5000/users"


    function loginUser(name, password) {
        fetch(baseUrl)
        .then(res => res.json())
        .then(users => {
          const user = users.find(user => user.name === name && user.password === password )
          if (user) {
            setUser(user)
            toast.success(`Welcome ${user.name}!`, {toastId: "2"}, {duration: "1000"})
            navigate('/')
          }
          else {
            toast.error("Invalid credentials!", {toastId: "3"},{duration: "1000"})
          }
        })
      }

    return <UserContext.Provider value={{
        user,
        loginUser,
        setUser,
        cartQuantity,
        setCartQuantity,
        products,
        setToCart,

    }}>
        {children}
    </UserContext.Provider>
}


export default UserProvider
