import React from 'react'
import { Link } from 'react-router-dom'
import { useState } from 'react';
import { useUser } from '../UserContext'; 


const Navbar = () => {
    const {logoutOpen,etLogoutOpen } = useState(false)
    const { user } = useUser();
    const [navOpen, setOpen] = useState(false)
    return (
        <div className='flex justify-between items-center bg-gray-800 px-5 z-20 text-white border-b-2 border-yellow-700 sticky inset-0 opacity-0.5 sm:px-28 py-5'>
            <h2 className='text-3xl'>E<span className='text-yellow-700 font-bold'>SHOP</span></h2>
            <div className='hidden sm:inline-flex gap-4 text-xl items-center'>
                { user ? (
                    <div>
                        <div className='flex items-center gap-3'>
                            <p className="text-white cursor-pointer">{user.name}</p>
                            <i className="fa-solid fa-caret-down cursor-pointer" onMouseOver={()=> setLogoutOpen(!logoutOpen)}></i>
                        </div>
                        { logoutOpen && (<div>
                            <Link to="/login">Log Out</Link>
                        </div>
                        )}
                    </div>
                    ):(
                    <div className='text-xl'>        
                    <i className="fa-solid fa-image-portrait"></i>
                    <Link to="/login">Login</Link>
                    </div>
                    )
                }
                <Link to="/">Home </Link>
                <Link to="/shop">Shop</Link>
            </div>
            <i className={`fa-solid ${!navOpen? 'fa-bars' : 'fa-xmark'} text-2xl inline-block sm:hidden cursor-pointer`} onClick={() => setOpen(!navOpen)}></i>

            {navOpen && (
            <div className='absolute left-0  top-16 w-full bg-gray-800 border-b-2 border-yellow-700 z-10 flex flex-col items-center gap-4 text-xl text-white py-5'>
                <div className='text-xl flex items-center'>        
                    <i className="fa-solid fa-image-portrait"></i>
                    <Link to="/login" className='block py-2'  onClick={() => setOpen(!navOpen)}>Login</Link>
                </div>
                <Link to="/" className='block py-2' onClick={() => setOpen(!navOpen)}>Home</Link>
                <Link to="/shop" className='block py-2' onClick={() => setOpen(!navOpen)}>Shop</Link>
            </div>
            )}
        </div>
    )
}

export default Navbar
